      })
  }

  updateBook = (book, shelf) => {
    BooksAPI.update(book, shelf)
      .then((books) => {
        this.setState((currentState) => ({
          books: currentState.books.concat([books])
        }))
      })
      .then(this.componentDidMount(
        BooksAPI.getAll()
      ))
  }

  render() {
    const books = this.state.books
    const currentlyReading = this.state.currentlyReading
    const read = this.state.read
    const wantToRead = this.state.wantToRead
    const changeShelf = this.updateBook

    return (
      <div className="app">
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <Route exact path='/search' render={() => (
            <SearchBar
              onChangeShelf={changeShelf}
              categorizedBooks={this.state.books}
            />
          )} />
          <Route exact path='/' render={() => (
            <BookTable
              currentlyReading={currentlyReading}
              read={read}
              wantToRead={wantToRead}
              onChangeShelf={changeShelf}
            />
          )} />
        </div>

        <div className="open-search">
        <Link to='/search'><button></button></Link>
        </div>
      </div>
    )
  }
}

export default BooksApp
