import React, { Component } from 'react'
import PropTypes from 'prop-types'
import ShowBook from './ShowBook'
import { Link, Route } from 'react-router-dom'

class ListBooks extends Component {
  static propTypes = {
    books: PropTypes.array,
    onUpdateShelf: PropTypes.func,
  }
  // state = {
  //   query: ''
  // }

  // updateQuery = (query) => {
  //   this.setState(() => ({
  //     query: query.trim()
  //   }))
  // }
  // clearQuery = () => {
  //   this.updateQuery('')
  // }


  render() {
   // const { query } = this.state
    const { books, onUpdateShelf } = this.props

    // const showingBooks = query === ''
    //   ? books
    //   : books.filter((b) => (
    //     b.title.toLowerCase().includes(query.toLowerCase())
    //   ))

    return (
      <div className="list-books">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <div className="list-books-content">
          <div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Currently Reading</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <ShowBook
                    books={books.filter(books => books.shelf === 'currentlyReading')}
                    onUpdateShelf={this.updateBook}
                  />
                )} />
              </div>
            </div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Want to Read</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <ShowBook

                    books={books.filter(books => books.shelf === 'wantToRead')}
                    onUpdateShelf={this.updateBook}

                  />
                )} />
              </div>
            </div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Read</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <ShowBook
                    onUpdateShelf={this.updateBook}
                    books={books.filter(books => books.shelf === 'read')}
                  />
                )} />
              </div>
            </div>

            <div className="bookshelf">
              <h2 className="bookshelf-title">Other</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <ListBooks
                    onUpdateShelf={this.updateBook}
                    books={books.filter(((c) => {
                      return c.shelf === "none"
                    }))}

                  />
                )}
                />
              </div>
            </div>

          </div>
        </div>
      </div>
    )
  }
}
export default ListBooks
