import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Link, Route } from 'react-router-dom'

class ListBooks extends Component {
  static propTypes = {
    books: PropTypes.array.isRequired,
    onUpdateShelf: PropTypes.func.isRequired,
  }
  state = {
    query: ''
  }

  updateQuery = (query) => {
    this.setState(() => ({
      query: query.trim()
    }))
  }
  clearQuery = () => {
    this.updateQuery('')
  }
  showShelf(book, onUpdateShelf) {
    return (
    <ol className='books-grid'>
      {this.props.books.map((Book) => (
        <li key={Book.id}>
          <div className="book">
            <div className="book-top">
              <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url(${Book.imageLinks.smallThumbnail})` }}></div>
              <div className="book-shelf-changer">
                <select value={Book.shelf} onChange={(event) => onUpdateShelf(Book, event.target.value)}>
                  <option value="move" disabled>Move to...</option>
                  <option value="currentlyReading" >Currently Reading</option>
                  <option value="wantToRead" >Want to Read</option>
                  <option value="read" >Read</option>
                  <option value="none">None</option>
                </select>
              </div>
            </div>
            <div className="book-title">{Book.title}</div>
            <div className="book-authors">{Book.authers}</div>
          </div>
        </li>
      ))}
    </ol>
    )
  }

  render() {
    const { query } = this.state
    const { books, onUpdateShelf } = this.props

    const showingBooks = query === ''
      ? books
      : books.filter((b) => (
        b.title.toLowerCase().includes(query.toLowerCase())
      ))

    return (
      <div className="list-books">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <div className="list-books-content">
          <div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Currently Reading</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <showShelf
                    books={books.filter(books => books.shelf === 'currentlyReading')}
                    onUpdateShelf={this.updateBook}
                  />
                )} />
              </div>
            </div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Want to Read</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <showShelf

                    books={books.filter(books => books.shelf === 'wantToRead')}
                    onUpdateShelf={this.updateBook}

                  />
                )} />
              </div>
            </div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Read</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <showShelf
                    onUpdateShelf={this.updateBook}
                    books={books.filter(books => books.shelf === 'read')}
                  />
                )} />
              </div>
            </div>

            <div className="bookshelf">
              <h2 className="bookshelf-title">Other</h2>
              <div className="bookshelf-books">
                <Route exact path='/' render={() => (
                  <ListBooks
                    onUpdateShelf={this.updateBook}
                    books={books.filter(((c) => {
                      return c.shelf === "none"
                    }))}

                  />
                )}
                />
              </div>
            </div>

          </div>
        </div>
      </div>
    )
  }
}
export default ListBooks
