import React, { Component } from 'react'
import * as BooksAPI from './BooksAPI'
import { Route } from 'react-router-dom'
import ListBooks from './ListBooks'
import ListBkResults from './ListBkResults'
import './App.css'

class BooksApp extends Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    query: '',
    showSearchPage: false,
    books: [],
    bkread: [],
    bkWantToRead: [],
    bkReading: [],
    bkResults: [],
    showingBooks: []
  }
  componentDidMount() {
    BooksAPI.getAll()
      .then((books) => {
        this.setState(() => ({
          books
        }))
      })
  }
  searchBook = (query) => {
    BooksAPI.search(query)
      .then((books) => {
        this.setState((currentState) => ({
          books: currentState.books.concat([books])
        }))
      })
  }
  //componentDidMount() {
  //  this.searchBook(this.state.query)
  //}

  removeBook = (books) => {
    this.setState((currentState) => ({
      books: currentState.books.filter((b) => {
        return b.id !== books.id
      })
    }))
  }

  shouldComponentUpdate() {
    return true
  }

  /*updateBook = (book, shelf) => {
    this.setState((currentState) => ({
      books: currentState.books
      //filter((c) => {
      //  return c.id !== contact.id
     // })
    }))

    BooksAPI.update(book, shelf)

    BooksAPI.update(book, shelf)
      .then((books) => {
        this.setState((previousState) => ({
          books: previousState.books.concat([books]),

        }))
      })

  }*/
  updateBook = (book, shelf) => {
    BooksAPI.update(book, shelf)
      .then((books) => {
        this.setState((currentState) => ({
          books: currentState.books.concat([books])
        }))
      })
  }


  render() {
    let { query, books, showingBooks , bkResults} = this.state

    // let showingBooks = []

    return (
      <div className="app">
        {this.state.showSearchPage ? (
          <div className="search-books">
            <div className="search-books-bar">
              <button className="close-search" onClick={() => this.setState({ showSearchPage: false })}>Close</button>
              <div className="search-books-input-wrapper">
                {/*
                  NOTES: The search from BooksAPI is limited to a particular set of search terms.
                  You can find these search terms here:
                  https://github.com/udacity/reactnd-project-myreads-starter/blob/master/SEARCH_TERMS.md

                  However, remember that the BooksAPI.search method DOES search by title or author. So, don't worry if
                  you don't find a specific author or title. Every search is limited by search terms.
                */}
                <input type="text" placeholder="Search by title or author"
                  value={query}
                  onChange={(event) => this.updateBook(event.target.value)}
                  books={this.searchBook(this.query)}
                />
              </div>
            </div>


            <div className="search-books-results">
              <span>Results for [{this.state.query}]: {this.bkResults.length} books.</span>
              <Route exact path='/' render={() => (
                <ListBkResults
                  bkResults={this.state.books}
                  //onUpdateShelf={this.updateBook}
                />
              )} />
            </div>
          </div>
        ) : (
            <div className="list-books">
              <div className="list-books-title">
                <h1>MyReads</h1>
              </div>
              <div className="list-books-content">
                <div>
                  <div className="bookshelf">
                    <h2 className="bookshelf-title">Currently Reading</h2>
                    <div className="bookshelf-books">
                      <Route exact path='/' render={() => (
                        <ListBooks
                          onUpdateShelf={this.updateBook}
                          books={this.state.books.filter(((c) => {
                            return c.shelf === "currentlyReading"
                          }))}

                        />
                      )} />

                    </div>
                  </div>
                  <div className="bookshelf">
                    <h2 className="bookshelf-title">Want to Read</h2>
                    <div className="bookshelf-books">
                      <Route exact path='/' render={() => (
                        <ListBooks
                          onUpdateShelf={this.updateBook}
                          books={this.state.books.filter(((c) => {
                            return c.shelf === "wantToRead"
                          }))}

                        />
                      )} />
                    </div>
                  </div>
                  <div className="bookshelf">
                    <h2 className="bookshelf-title">Read</h2>
                    <div className="bookshelf-books">
                      <Route exact path='/' render={() => (
                        <ListBooks
                          onUpdateShelf={this.updateBook}
                          books={this.state.books.filter(((c) => {
                            return c.shelf === "read"
                          }))}

                        />
                      )} />
                    </div>
                  </div>
                </div>
              </div>
              <div className="open-search">
                <button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>
              </div>
            </div>
          )}
      </div>
    )
  }
}

export default BooksApp
