import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'

function ListBooks(props){
  let selected1 = '';
  let selected2 = '';
  let selected3 = '';
return(
<ol className='books-grid'>
{props.books.map((Books) => (



    <li>
      <div className="book">
        <div className="book-top">
          <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url(${Books.imageLinks.smallThumbnail})` }}></div>
          <div className="book-shelf-changer">
            <select>
              <option value="move" disabled>Move to...</option>
              <option value="currentlyReading" selected={Books.shelf==='currentlyReading'? true:false} >Currently Reading</option>
              <option value="wantToRead" selected2={Books.shelf==='currentlyReading'? true:false} >Want to Read</option>
              <option value="read" selected3={Books.shelf==='currentlyReading'? true:false} >Read</option>
              <option value="none">None</option>
            </select>
          </div>
        </div>
        <div className="book-title">{Books.title}</div>
        <div className="book-authors">{Books.authers}</div>
      </div>
    </li>


))}
</ol>

)
}

